#!/usr/bin/python
#PARNAME=start #LOC=1,1 #PARNAME=end #LOC=2,1 #PARNAME=row_length #LOC=3,1 #PARNAME=offset #LOC=4,1 #PARNAME=min #LOC=5,1 #PARNAME=max #LOC=6,1
def connectionFunc( srclocs, dstlocs, start, end, rowlen, offset, min, max ):
import math
i = 0 out = [] new_size=end-start size_diff = rowlen-new_size for srcloc in srclocs: if i%rowlen >= start and i%rowlen < end and i>=min and i<max: conn = (i,i-min+offset-math.floor((i-min)/rowlen)*size_diff,0) out.append(conn) i = i + 1 return out

